insert into jogosi(nome, descricao, preco, imagem_url) values('Super Sávio World', 'Venha ajudar nosso herói Sávio a resgatar a princesa Ticianne do Castelo do poderoso Dirson.', 129.90, '/imagens/superSavioWorld.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Undertale', 'Undertale é um aclamado RPG independente onde suas escolhas entre lutar ou poupar monstros alteram drasticamente o enredo e o final do jogo.', 20.00,'/imagens/Undertale.JPG');
insert into jogosi(nome, descricao, preco, imagem_url) values('Omori', 'Omori é um jogo eletrônico de RPG de 2020 desenvolvido pelo estúdio indie Omocat. Baseado na série de webcomics do diretor, Omori, há destaque para conceitos como ansiedade e depressão e elementos de terror psicológico.', 37.90, '/imagens/omori.jpg') ;
insert into jogosi(nome, descricao, preco, imagem_url) values('Cuphead', 'Jogo de ação e tiros (run and gun) com estilo visual inspirado nas animações da década de 1930, conhecido por sua dificuldade desafiadora.', 50.00,'/imagens/cuphead.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Silksong', 'Sequência do aclamado metroidvania Hollow Knight, lançada em 4 de setembro de 2025.', 60.00,'/imagens/silksong.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Hollow Knight', 'Jogo de ação e aventura 2D do gênero metroidvania ambientado em Hallownest, um reino subterrâneo em ruínas.', 55.00,'/imagens/hollowknight.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Skull The Hero Slayer', 'Jogo de plataforma 2D com elementos rogue-lite onde Skul deve resgatar o Rei Demônio, usando seu poder de trocar caveiras.', 53.00,'/imagens/Skull.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('The Witch’s House', 'Jogo de terror e quebra-cabeça feito no RPG Maker VX, famoso por sua atmosfera tensa e reviravolta marcante', 45.00,'/imagens/witchshouse.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('One Shot', 'OneShot é um jogo surreal de quebra-cabeça/aventura com visão de cima e mecânicas únicas. Você deve guiar uma criança por um mundo misterioso em uma missão para restaurar seu sol há muito extinto. O mundo sabe que você existe.', 32.90,'/imagens/OneShot.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Celeste', 'Ajude Madeline a sobreviver aos seus demônios interiores em sua jornada até o topo da Montanha Celeste, neste jogo de plataforma super preciso dos criadores de TowerFall.', 59.90,'/imagens/Celeste.jpg');
insert into jogosi(nome, descricao, preco, imagem_url) values('Camp Buddy', 'Faça amizades intensas em um acampamento cheio de atividades e descobertas.', 69.00,'/imagens/Camp_Buddy.jpg');
