insert into jogosi(nome, descricao, preco, imagem_url) values('Among Us', 'Jogo multijogador online de dedução social, onde tripulantes tentam completar tarefas enquanto impostores sabotam tudo.', 6.00,'/imagens/among-us.jpg');
