insert into jogosr  (nome, descricao, preco, imagem_url) values('Bomberman', 'Jogo de ação e estratégia onde você deve posicionar bombas para destruir obstáculos e derrotar inimigos em labirintos.', 0.00,'/imagens/bomberman.jpg');
