insert into jogosr  (nome, descricao, preco, imagem_url) values('Super Bomberman 4', 'Considerado por muitos o melhor Bomberman já feito, trazendo fases criativas, power-ups divertidos e multiplayer frenético.', 0.00, '/imagens/superBomberman4.jpg');
